from langflow.schema import Record
import openpyxl
import csv

class XLSXComponent(CustomComponent):
    display_name = "XLSXComponent"
    description = "Use as a template to create your own component."
    documentation: str = "http://docs.langflow.org/components/custom"
    icon = "custom_components"

    def build_config(self):
        return {
            "path": {
                "display_name": "Path",
            },
        }

    def load_file(self, path: str, silent_errors: bool = False) -> Record:
        resolved_path = self.resolve_path(path)
        path_obj = Path(resolved_path)
        extension = path_obj.suffix[1:].lower()
        if extension == "doc":
            raise ValueError("doc files are not supported. Please save as .docx")
        if extension not in TEXT_FILE_TYPES:
            raise ValueError(f"Unsupported file type: {extension}")
        record = parse_text_file_to_record(resolved_path, silent_errors)
        self.status = record if record else "No data"
        return record or Record()

    def build(self, path :str) -> str:
        workbook = openpyxl.load_workbook(path, data_only=True)

        records = []

        for worksheet, name in zip(workbook.worksheets,workbook.sheetnames):
            with open(f'{name}.csv', 'w', newline='', encoding='utf-8') as csvfile:
                csv_writer = csv.writer(csvfile, delimiter='|')
                for row in worksheet.iter_rows(values_only=True):
                    wrow = ['\t' if cell is None else cell for cell in row]
                    while wrow and wrow[-1] == '\t':
                        wrow.pop()
                    csv_writer.writerow(wrow)

            records.append(self.load_file(f'{name}.csv'))


        return records[0]

